import{_ as d,c as e,o as a,ae as o}from"./chunks/framework.CzKctL2r.js";const r="/SDUCSGuide_test/assets/image%20(12).DZRIScE1.png",u=JSON.parse('{"title":"🧠 AI 使用指南","description":"","frontmatter":{},"headers":[],"relativePath":"pages/1sheng-cun-gong-le/ai-shi-yong-zhi-nan.md","filePath":"pages/1sheng-cun-gong-le/ai-shi-yong-zhi-nan.md"}'),n={name:"pages/1sheng-cun-gong-le/ai-shi-yong-zhi-nan.md"};function i(h,t,s,l,g,p){return a(),e("div",null,t[0]||(t[0]=[o('<h1 id="🧠-ai-使用指南" tabindex="-1">🧠 AI 使用指南 <a class="header-anchor" href="#🧠-ai-使用指南" aria-label="Permalink to &quot;🧠 AI 使用指南&quot;">​</a></h1><blockquote><p>你的第2，3，4，5...个大脑</p></blockquote><h2 id="一、总体建议-人可以做到的-ai都可以" tabindex="-1">一、总体建议：人可以做到的，AI都可以 <a class="header-anchor" href="#一、总体建议-人可以做到的-ai都可以" aria-label="Permalink to &quot;一、总体建议：人可以做到的，AI都可以&quot;">​</a></h2><h4 id="日常用法场景" tabindex="-1">日常用法场景： <a class="header-anchor" href="#日常用法场景" aria-label="Permalink to &quot;日常用法场景：&quot;">​</a></h4><ul><li>🔧 编程辅助（调试、代码补全、查API）</li><li>📚 学习辅导（概念解释、例题推导）</li><li>🧠 灵感激发（算法优化、prompt写法、架构设计）</li><li>🗂 数据处理（数据清洗、格式转换、评估脚本）</li></ul><hr><h2 id="二、ai助手能力详细对比表格-2025-年-5-月更新" tabindex="-1"><strong>二、AI助手能力详细对比表格</strong>（2025 年 5 月更新） <a class="header-anchor" href="#二、ai助手能力详细对比表格-2025-年-5-月更新" aria-label="Permalink to &quot;**二、AI助手能力详细对比表格**（2025 年 5 月更新）&quot;">​</a></h2><p>表格由gpt4o调研生成，可能存在一定错误。</p><table tabindex="0"><thead><tr><th>助手名称</th><th>模型/公司</th><th>模型能力层级</th><th>中文支持</th><th>编程支持</th><th>长文本处理</th><th>图像支持</th><th>优势亮点</th><th>明显劣势</th><th>是否付费</th><th>推荐用途</th></tr></thead><tbody><tr><td><strong>ChatGPT (GPT-4o)</strong></td><td>OpenAI</td><td>GPT-4o（128k上下文，多模态）</td><td>⭐⭐⭐⭐</td><td>⭐⭐⭐⭐</td><td>⭐⭐⭐⭐</td><td>✅ 多模态强</td><td>多轮对话稳定，代码/写作表现优异，图表和视觉输入支持好</td><td>免费版模型弱，中文稍有“AI味”</td><td>✅（Plus）</td><td>编程开发、论文写作、图文数据分析</td></tr><tr><td><strong>Grok</strong></td><td>xAI (Elon Musk)</td><td>Grok-1.5</td><td>⭐⭐</td><td>⭐⭐</td><td>⭐⭐</td><td>❌</td><td>Twitter/X 深度集成，网络信息总结能力强</td><td>编程弱、不能处理复杂推理</td><td>❌（免费）</td><td>数据总结、舆情分析、趋势追踪</td></tr><tr><td><strong>Gemini</strong></td><td>Google DeepMind</td><td>Gemini 1.5 Pro（128k多模态）</td><td>⭐⭐⭐</td><td>⭐⭐</td><td>⭐⭐⭐</td><td>✅ 图像/文档强</td><td>图文理解能力最强，支持浏览器搜索，适合学术辅助</td><td>编程能力不稳定，接口体验略逊</td><td>❌（免费）</td><td>图文总结、学术报告、网页分析</td></tr><tr><td><strong>DeepSeek</strong></td><td>深度求索（DeepSeek）</td><td>DeepSeek-Coder / VL</td><td>⭐⭐⭐⭐</td><td>⭐⭐⭐⭐</td><td>⭐⭐</td><td>✅（VL）</td><td>中文代码生成强，文档感知良好，开发者体验类GPT</td><td>英文表现弱，部分多模态功能未开放</td><td>❌（免费）</td><td>中文编程、开发助手、技术文档生成</td></tr><tr><td><strong>豆包</strong></td><td>字节跳动</td><td>自研豆包大模型</td><td>⭐⭐⭐⭐</td><td>⭐⭐</td><td>⭐⭐</td><td>❌</td><td>中文语境自然，语气亲切，适合非技术类用户</td><td>专业知识不足，编程/推理任务表现弱</td><td>❌（免费）</td><td>日常问答、知识问询、课程笔记生成</td></tr><tr><td><strong>Kimi Chat</strong></td><td>月之暗面实验室（Moonshot AI）</td><td>Yi-34B / Yi-1.5系列</td><td>⭐⭐⭐⭐</td><td>⭐⭐</td><td>⭐⭐⭐⭐⭐（200k+）</td><td>❌</td><td>超长上下文支持（&gt;200k），长文处理第一，中文表达自然</td><td>英文不稳定，缺乏多模态能力</td><td>❌（免费）</td><td>长文阅读、合同/论文理解、摘要生成</td></tr><tr><td><strong>Claude</strong></td><td>Anthropic</td><td>Claude 3.5 Sonnet / Opus</td><td>⭐⭐⭐</td><td>⭐⭐⭐⭐</td><td>⭐⭐⭐⭐（200k）</td><td>✅（部分模型）</td><td>推理强，表达清晰自然，适合结构化输入任务</td><td>国内需代理，中文表达弱于GPT / DeepSeek</td><td>✅（Pro/Max）</td><td>高级写作、结构理解、分析推理</td></tr></tbody></table><hr><h3 id="✅-几个术语说明" tabindex="-1">✅ 几个术语说明 <a class="header-anchor" href="#✅-几个术语说明" aria-label="Permalink to &quot;✅ 几个术语说明&quot;">​</a></h3><ul><li><strong>模型能力层级</strong>：指语言模型在当前阶段的整体理解、推理、生成能力，非商业定价。</li><li><strong>中文支持</strong>：指中文上下文理解、回答流畅度与技术专业性。</li><li><strong>编程支持</strong>：是否能进行代码补全、调试、解释等任务。</li><li><strong>长文本处理</strong>：是否能处理超过1万字以上的输入（如论文、书籍、代码仓库）。</li><li><strong>图像支持</strong>：能否看图、看表格并据此进行分析或问答。</li></ul><hr><h2 id="三、个人使用体验" tabindex="-1">三、个人使用体验 <a class="header-anchor" href="#三、个人使用体验" aria-label="Permalink to &quot;三、个人使用体验&quot;">​</a></h2><p><strong>Jo：</strong></p><blockquote><p>本人是ChatGPT的忠实信徒，并开通了PLUS套餐，套餐中各模型使用限额见下图。事实上在赶ddl期间，即便是每天高强度使用gpt进行代码修改以及领域调研，也很难达到限额（但可能会偷偷降智）。考虑到每月20🔪的花费，多人拼车使用性价比最高，但也需要注意更新后ChatGPT全局记忆共享，注重隐私的话不建议拼车。</p><p><img src="'+r+'" alt=""></p><p>综合来看，我其实所有需求都会在ChatGPT上实现，辅以DeepSeek，其他AI使用的并不多，Gemini和Grok均是为了白嫖”深度搜索“的功能，豆包偶尔进行文献翻译，Claude容易封号所以一直没有使用。</p><p>需要注意的是，并不是所有需求都适合进行“深度思考”与“网络搜索”。我认为可以把AI想象成人类来进行选择，有的问题需要苦思冥想，有的问题需要内化的知识但无需思考，有的问题对准确性的要求需要查阅外部资料……此外，即便是同一个问题，每个人的表达方式（Prompt）不同也会导向AI不同的回答，如何让模型给出你想要的回答也是一种重要的能力。多说无益，实践是最好的老师。</p><p>“尽信书不如无书”，尽信AI不如不用AI。无论何时都不要放弃自己的思考，AI固然能力强大，但是并不意味着它每次都会给出漂亮简洁的回答，有时答案的幻觉也会十分严重。保持质疑的习惯，保持敏锐的观察，不止在使用AI。</p></blockquote>',16)]))}const k=d(n,[["render",i]]);export{u as __pageData,k as default};
