import{_ as s,c as t,o as r,j as e,a}from"./chunks/framework.CzKctL2r.js";const u=JSON.parse('{"title":"CS自学指南","description":"","frontmatter":{},"headers":[],"relativePath":"pages/1sheng-cun-gong-le/cs-zi-xue-zhi-nan/README.md","filePath":"pages/1sheng-cun-gong-le/cs-zi-xue-zhi-nan/README.md"}'),o={name:"pages/1sheng-cun-gong-le/cs-zi-xue-zhi-nan/README.md"};function c(i,n,l,p,d,g){return r(),t("div",null,n[0]||(n[0]=[e("h1",{id:"cs自学指南",tabindex:"-1"},[a("CS自学指南 "),e("a",{class:"header-anchor",href:"#cs自学指南","aria-label":'Permalink to "CS自学指南"'},"​")],-1),e("p",null,[a("在生存版块，不得不提到大名鼎鼎的"),e("a",{href:"https://csdiy.wiki/",target:"_blank",rel:"noreferrer"},"CS自学指南"),a("，这是一个涵盖了计算机学科各个领域全世界最优秀课程的指南，无论你对哪个方向感兴趣，都可以找到对应的优秀课程，且在每个课程版块，也会有同学组队学习。这些课程有一些共同的特点，那就是足够深入，实践与理论相结合，他们的实验一般都是一个小项目，如果认真完成，你将会对一个领域有了从细节到宏观的深度掌握。")],-1),e("p",null,"也许你会疑惑，这些课程也大都在我们的培养体系中，也是一些必修课，那为什么还要去自学一些额外的课程呢？这就涉及到国内专业课大都抱残守阙，学习的内容大都是很老旧的东西，老师一般也不会介绍这个领域比较前沿的问题，这种学习往往是纯粹的泡在一堆概念中，也许你理解了他的含义，可是课程也难以交给你在实际环境中他有什么作用。想必你也不想一直学一些豆瓣评分高达5.7/10的全国优秀教材吧 🐶。",-1),e("p",null,"就拿操作系统的进程调度为例，课本中的调度策略都是极其naive的策略，比如轮询、FIFO、LRU等等。但是在今天，我们的处理器不光是简单的多核，核心与核心之间往往差别巨大，有大核、小核，在手机CPU中往往还集成了NPU、GPU，课本上的调度策略明显不能胜任今天的情况。虽说初步学习我们也不可能去解决今天的业界难题，但是优秀的课程应该能够打开我们的视野，让我们知道这个领域深入研究下去我们会面临哪些难题，以及在一个接近实际的环境，如何应用这一领域的知识，而不是止于概念和仿真。",-1)]))}const f=s(o,[["render",c]]);export{u as __pageData,f as default};
